window.dataLayer = window.dataLayer || [];
function gtag() { dataLayer.push(arguments); }

// Configuración inicial del Consent Mode: denegado por defecto
gtag('consent', 'default', {
  'ad_storage': 'denied',
  'analytics_storage': 'denied',
  'wait_for_update': 500
});

js.cookies-banner.js:
document.addEventListener("DOMContentLoaded", function () {
  if (!localStorage.getItem("cookiesDecision")) {
    const banner = document.createElement("div");
    banner.id = "cookieBanner";
    banner.className = "alert alert-dark fixed-bottom text-center mb-0";
    banner.innerHTML = `
      Este sitio utiliza cookies para análisis y funcionalidad. 
      <a href="privacidad.html" class="text-warning text-decoration-underline">Más información</a>.
      <br>
      <button id="aceptarCookies" class="btn btn-success btn-sm mt-2 me-2">Aceptar</button>
      <button id="rechazarCookies" class="btn btn-outline-light btn-sm mt-2">Rechazar</button>
    `;
    document.body.appendChild(banner);

    // Aceptar cookies
    document.getElementById("aceptarCookies").addEventListener("click", function () {
      gtag('consent', 'update', {
        'ad_storage': 'granted',
        'analytics_storage': 'granted'
      });
      localStorage.setItem("cookiesDecision", "accepted");
      banner.remove();
    });

    // Rechazar cookies
    document.getElementById("rechazarCookies").addEventListener("click", function () {
      gtag('consent', 'update', {
        'ad_storage': 'denied',
        'analytics_storage': 'denied'
      });
      localStorage.setItem("cookiesDecision", "rejected");
      banner.remove();
    });
  }
});

js.lang-redirect.js:
document.addEventListener("DOMContentLoaded", function () {
  const lang = navigator.language || navigator.userLanguage;

  const isEnglish = lang.toLowerCase().startsWith('en');
  const currentPath = window.location.pathname;

  // Redirige solo si estás en una versión neutral o incorrecta
  if (currentPath.endsWith('/privacidad.html') && isEnglish) {
    window.location.href = 'privacy.html';
  } else if (currentPath.endsWith('/privacy.html') && lang.toLowerCase().startsWith('es')) {
    window.location.href = 'privacidad.html';
  }
});

js.language-toggle.js:
document.addEventListener("DOMContentLoaded", function () {
  const lang = document.documentElement.lang;

  const button = document.createElement("div");
  button.id = "languageToggle";
  button.textContent = lang.startsWith("es") ? "🌐 View in English" : "🌐 Ver en español";
  Object.assign(button.style, {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    backgroundColor: "#343a40",
    color: "white",
    padding: "8px 12px",
    borderRadius: "30px",
    fontSize: "14px",
    cursor: "pointer",
    zIndex: "9999",
    boxShadow: "0 2px 6px rgba(0,0,0,0.3)",
    fontFamily: "inherit",
    userSelect: "none"
  });

  button.addEventListener("click", function () {
    if (lang.startsWith("es")) {
      window.location.href = "privacy.html";
    } else {
      window.location.href = "privacidad.html";
    }
  });

  document.body.appendChild(button);
});
