document.addEventListener('DOMContentLoaded', () => {
    const form = document.getElementById('loginForm');
  
    if (form) {
      form.addEventListener('submit', async (e) => {
        e.preventDefault();
  
        const email = document.getElementById('email').value;
        const password = document.getElementById('password').value;
  
        try {
          const response = await fetch('/api/login', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ email, password })
          });
  
          const data = await response.json();
  
          if (response.ok) {
            // Si login exitoso, guardar token y redirigir
            localStorage.setItem('jwtToken', data.token);
            window.location.href = 'index.html';
          } else if (response.status === 403 && data.error.includes('no verificada')) {
            // Mostrar el modal en lugar de alert si la cuenta no est√° verificada
            mostrarModalVerificacion();
          } else {
            // Mostrar otros errores
            alert(`‚ùå Error: ${data.error || 'Ocurri√≥ un error inesperado'}`);
          }
        } catch (error) {
          console.error('‚ùå Error en login:', error);
          alert('‚ùå Error de conexi√≥n. Intent√° nuevamente m√°s tarde.');
        }
      });
    }
  });

