const { verifyToken } = require('../utils/jwt');

// ‚úÖ Middleware para proteger rutas y verificar el token
function verificarToken(req, res, next) {
  const authHeader = req.headers['authorization'];

  if (!authHeader) {
    console.warn('‚ö†Ô∏è Token no proporcionado en el header Authorization');
    return res.status(401).json({ error: 'Token no proporcionado' });
  }

  const token = authHeader.replace('Bearer ', '').trim();

  if (!token) {
    console.warn('‚ö†Ô∏è Token vac√≠o o mal formado despu√©s de limpiar Bearer');
    return res.status(401).json({ error: 'Token inv√°lido o vac√≠o' });
  }

  try {
    const decoded = verifyToken(token);
    if (!decoded) {
      console.warn('‚ö†Ô∏è Token decodificado vac√≠o');
      return res.status(401).json({ error: 'Token inv√°lido' });
    }

    req.user = decoded; // Guardamos los datos del usuario en el request
    next(); // Continuamos con la siguiente funci√≥n
  } catch (error) {
    console.error('‚ùå Error al verificar token:', error.message);
    return res.status(401).json({ error: 'Token inv√°lido o expirado' });
  }
}

module.exports = verificarToken;

