css.style.css:
body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 20px;
  background-image: url("../images/fondooficion.JPG");

 /* Ruta a la imagen */
  background-size: cover; /* Ajusta la imagen para que cubra toda la pantalla */
  background-position: center; /* Centra la imagen */
  background-attachment: fixed; /* Hace que el fondo no se desplace al hacer scroll */
  background-repeat: no-repeat; /* Evita que la imagen se repita */
}
label {
  color: #000; /* Negro puro */
  font-weight: bold; /* Negrita */
}


h1 {
  
  color: black; /* Texto negro */
  padding: 10px 20px;
  
  border-radius: 10px;
  margin: 20px auto;
  text-align: center;
  text-shadow:  
    -2px -2px 0 white,  
     2px -2px 0 white,  
    -2px  2px 0 white,  
     2px  2px 0 white; /* Borde blanco */
}
h1:hover {
  animation: shake-vertical 0.8s linear both;
  
}

@keyframes shake-vertical {
  0%, 100% { transform: translateY(0); }
  10%, 30%, 50%, 70% { transform: translateY(-8px); }
  20%, 40%, 60% { transform: translateY(8px); }
  80% { transform: translateY(6.4px); }
  90% { transform: translateY(-6.4px); }
}
button {
  background: #fbca1f;
  font-family: inherit;
  padding: 0.6em 1.3em;
  font-weight: 900;
  font-size: 18px;
  border: 3px solid black;
  border-radius: 0.4em;
  box-shadow: 0.1em 0.1em;
  cursor: pointer;
  transition: all 0.1s ease-in-out;
}

button:hover {
  transform: translate(-0.05em, -0.05em);
  box-shadow: 0.15em 0.15em;
}

button:active {
  transform: translate(0.05em, 0.05em);
  box-shadow: 0.05em 0.05em;
}


@keyframes rotate-center {
  0% { transform: rotate(0); }
  100% { transform: rotate(360deg); }
}


form {
  margin-bottom: 20px;
  text-align: center;
}



/* Contenedor principal que agrupa las secciones */
.container {
  flex-wrap: wrap;
  display: flex; /* Hace que las secciones est√©n una al lado de la otra */
  justify-content: space-between; /* Espaciado entre secciones */
  gap: 20px; /* Espacio entre ellas */
  padding: 20px;
  align-items: flex-start; /* Alinea las secciones al inicio */
}

/* Estilos generales para cada secci√≥n */
.section {
  flex: 1; /* Hace que ambas secciones ocupen el mismo espacio */
  background-color: #fff;
  border: 4px solid #000000;
  padding: 15px;
  margin-bottom: 20px;
  border-radius: 20px;
  min-width: 250px; /* Evita que las secciones se encojan demasiado */
  text-align: center; /* Centra el contenido */
}

/* Ajustar tama√±o de los inputs */
input {
  width: 100%;
  max-width: 300px;
  box-sizing: border-box;
  margin-bottom: 10px;
  display: block;
  margin-left: auto;
  margin-right: auto;
}


.scale-up-horizontal-center {
  animation: scale-up-horizontal-center 0.4s;
}

@keyframes scale-up-horizontal-center {
  0% {
     transform: scaleX(0.2);
  }
  100% {
     transform: scaleX(1);
  }
}
#recipeSummary {
  background: linear-gradient(135deg, #e5ece6, #107a04); /* Degradado verde */
  color: white; /* Para mejorar la visibilidad del texto */
}

#calculationDetails {
  background: linear-gradient(135deg, #e8ebee, #0D47A1); /* Degradado azul */
  color: white;
}
#recipeSummary:hover {
  background: linear-gradient(135deg, #66bb6a, #2e7d32);
}

#calculationDetails:hover {
  background: linear-gradient(135deg, #64b5f6, #1e88e5);
}

/* Hacer responsive */
@media (max-width: 768px) {
  .container {
    flex-direction: column;
    align-items: center;
    padding: 10px;
  }

  .section {
    width: 100%;
    padding: 15px;
    margin-bottom: 20px;
  }
  
}
footer img {
  transition: transform 0.3s ease;
}

footer img:hover {
  transform: scale(1.1);
}
/* Estilo personalizado para modal chico alineado a la izquierda */
.modal-custom-left {
  max-width: 400px;       /* M√°s angosto */
  margin: 1rem auto 1rem 1rem; /* Espaciado alineado a la izquierda */
}

@media (min-width: 768px) {
  .modal-custom-left {
    margin-left: 2rem;
  }
}

/* Opcional: achicar altura del video */
.modal-body iframe {
  height: 200px;
}

